package org.hibernate.export;import org.hibernate.cfg.Configuration;import org.hibernate.tool.hbm2ddl.SchemaExport;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.orm.hibernate3.LocalSessionFactoryBean;import org.springframework.test.context.ContextConfiguration;import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;/** * Maintains DB schema * * @author marc */@RunWith(SpringJUnit4ClassRunner.class)@ContextConfiguration(locations={"classpath:bundle-context-test.xml"})public class SchemaMaintenanceTest  {	@Autowired    private LocalSessionFactoryBean localSessionFactoryBean;    @Test    public void testExportDDL() {        Configuration configuration = localSessionFactoryBean.getConfiguration();        SchemaExport export = new SchemaExport(configuration);        export.setOutputFile("schmema.ddl");        export.setDelimiter(";");        export.execute(false, false, false, false);    }    public void setLocalSessionFactoryBean(LocalSessionFactoryBean localSessionFactoryBean) {        this.localSessionFactoryBean = localSessionFactoryBean;    }}